const App: () => React$Node = () => {
  const fadeAnim = useRef(new Animated.Value(1)).current

  React.useEffect(() => {
    Animated.timing(
      fadeAnim,
      {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true
      }
    ).start()
  }, [fadeAnim])
  
  return (
    <>
      <StatusBar barStyle="dark-content" />
      <SafeAreaView>
        <View style={styles.mainContainer}>
          <View style={styles.background}>
            <Animated.Image source = {require('./assets/Aqusafe home.png')} style={{
                    position: 'absolute',
                    width: '100%',
                    height: '100%',
                    opacity: fadeAnim,
                  }}/>
          </View>
        </View>
      </SafeAreaView>
    </>
  );
};

